mapper.select:
SELECT id, agent_id, agent_name, description, channel, status, create_time, update_time
FROM ai_agent
WHERE id = #{id}

mapper.update:
UPDATE ai_agent
SET agent_id = #{agentId}, agent_name = #{agentName}, description = #{description}, channel = #{channel},
    status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
