mapper.select:
SELECT id, agent_id, task_name, description, cron_expression, task_param, status, create_time, update_time
FROM ai_agent_task_schedule
WHERE id = #{id}

mapper.update:
UPDATE ai_agent_task_schedule
SET agent_id = #{agentId}, task_name = #{taskName}, description = #{description}, cron_expression = #{cronExpression},
    task_param = #{taskParam}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
