mapper.select:
SELECT id, api_id, base_url, api_key, completions_path, embeddings_path, status, create_time, update_time
FROM ai_client_api
WHERE id = #{id}

mapper.update:
UPDATE ai_client_api
SET api_id = #{apiId}, base_url = #{baseUrl}, api_key = #{apiKey}, completions_path = #{completionsPath},
    embeddings_path = #{embeddingsPath}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
