mapper.select:
SELECT id, source_type, source_id, target_type, target_id, ext_param, status, create_time, update_time
FROM ai_client_config
WHERE id = #{id}

mapper.update:
UPDATE ai_client_config
SET source_type = #{sourceType}, source_id = #{sourceId}, target_type = #{targetType}, target_id = #{targetId},
    ext_param = #{extParam}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
