mapper.select:
SELECT id, client_id, client_name, description, status, create_time, update_time
FROM ai_client
WHERE id = #{id}

mapper.update:
UPDATE ai_client
SET client_id = #{clientId}, client_name = #{clientName}, description = #{description}, status = #{status},
    create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
