mapper.select:
SELECT id, model_id, api_id, model_name, model_type, status, create_time, update_time
FROM ai_client_model
WHERE id = #{id}

mapper.update:
UPDATE ai_client_model
SET model_id = #{modelId}, api_id = #{apiId}, model_name = #{modelName}, model_type = #{modelType},
    status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
