mapper.select:
SELECT id, rag_id, rag_name, knowledge_tag, status, create_time, update_time
FROM ai_client_rag_order
WHERE id = #{id}

mapper.update:
UPDATE ai_client_rag_order
SET rag_id = #{ragId}, rag_name = #{ragName}, knowledge_tag = #{knowledgeTag}, status = #{status},
    create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
