mapper.select:
SELECT id, prompt_id, prompt_name, prompt_content, description, status, create_time, update_time
FROM ai_client_system_prompt
WHERE id = #{id}

mapper.update:
UPDATE ai_client_system_prompt
SET prompt_id = #{promptId}, prompt_name = #{promptName}, prompt_content = #{promptContent}, description = #{description},
    status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
