mapper.select:
SELECT id, mcp_id, mcp_name, transport_type, transport_config, request_timeout, status, create_time, update_time
FROM ai_client_tool_mcp
WHERE id = #{id}

mapper.update:
UPDATE ai_client_tool_mcp
SET mcp_id = #{mcpId}, mcp_name = #{mcpName}, transport_type = #{transportType}, transport_config = #{transportConfig},
    request_timeout = #{requestTimeout}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
